mapper.select:

select * from(
	select rownum as rn, a.* from
	(select r.rnum, r.bno, reportContent, reporter, handle, b.title, content, writer
    from hos_board_report r left outer join hos_board b on r.bno = b.bno order by r.rnum)a where rownum <= #{criteria.maxRow}
) where rn > #{criteria.minRow}


select * from(
	select rownum as rn, a.* from
	(select r.rnum, r.bno, reportContent, reporter, handle, b.title, content, writer
    from hos_board_report r left outer join hos_board b on r.bno = b.bno where reporter = #{reporter} order by r.rnum)a where rownum <= #{criteria.maxRow}
) where rn > #{criteria.minRow}
